import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/CasePage.css'; // Assuming you want to use the same styles

const GamepadPage = () => {
  const gamepadData = [
    { id: '67ff0f02173e97a1a082e4cb', name: 'Xbox Wireless Controller', image: 'data:image/jpeg;base64,', slug: 'xbox-wireless-controller' },
  { id: '67ff0f02173e97a1a082e4cc', name: 'PlayStation DualSense', image: 'data:image/jpeg;base64,', slug: 'playstation-dualsense' },
  { id: '67ff0f02173e97a1a082e4cd', name: '8BitDo Pro 2', image: 'data:image/jpeg;base64,', slug: '8bitdo-pro-2' },
  { id: '67ff0f02173e97a1a082e4ce', name: 'Razer Wolverine V2 Chroma', image: 'data:image/jpeg;base64,', slug: 'razer-wolverine-v2-chroma' },
  { id: '67ff0f02173e97a1a082e4cf', name: 'Logitech F710', image: 'data:image/jpeg;base64,', slug: 'logitech-f710' },

  ];

  return (
    <div className="gamepad-page">
      <h2>Browse Gamepads</h2>
      <div className="gamepad-grid">
        {gamepadData.map((gamepad) => (
          <Link to={`/product/${gamepad.id}`} key={gamepad.id} className="gamepad-card">
            <img src={gamepad.image} alt={gamepad.name} />
            <h3>{gamepad.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default GamepadPage;
