import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/PeripheralsPage.css'; // Import the CSS file

const LaptopPage = () => {
  const laptopData = [
    { id: '67ff107930df75e438f334ad', name: 'Dell XPS 15', image: 'data:image/jpeg;base64,', slug: 'dell-xps-15' },
  { id: '67ff107930df75e438f334ae', name: 'Apple MacBook Pro 16-inch', image: 'data:image/jpeg;base64,', slug: 'apple-macbook-pro-16-inch' },
  { id: '67ff107930df75e438f334af', name: 'HP Spectre x360', image: 'data:image/jpeg;base64,', slug: 'hp-spectre-x360' },
  { id: '67ff107930df75e438f334b0', name: 'Lenovo ThinkPad X1 Carbon', image: 'data:image/jpeg;base64,', slug: 'lenovo-thinkpad-x1-carbon' },
  { id: '67ff107930df75e438f334b1', name: 'ASUS ROG Zephyrus G14', image: 'data:image/jpeg;base64,', slug: 'asus-rog-zephyrus-g14' },

  ];

  return (
    <div className="laptop-page">
      <h2>Browse Laptops</h2>
      <div className="laptop-grid">
        {laptopData.map((laptop) => (
          <Link to={`/product/${laptop.id}`} key={laptop.id} className="laptop-card">
            <img src={laptop.image} alt={laptop.name} />
            <h3>{laptop.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default LaptopPage;
