import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/PeripheralsPage.css'; // Assuming you want to use the same styles


const MicrophonePage = () => {
  const microphoneData = [
    { id: '67ff0f62173e97a1a082e4d5', name: 'Blue Yeti', image: 'data:image/jpeg;base64,', slug: 'blue-yeti' },
  { id: '67ff0f62173e97a1a082e4d6', name: 'HyperX QuadCast S', image: 'data:image/jpeg;base64,', slug: 'hyperx-quadcast-s' },
  { id: '67ff0f62173e97a1a082e4d7', name: 'Rode NT-USB+', image: 'data:image/jpeg;base64,', slug: 'rode-nt-usb' },
  { id: '67ff0f62173e97a1a082e4d8', name: 'Shure MV7', image: 'data:image/jpeg;base64,', slug: 'shure-mv7' },
  { id: '67ff0f62173e97a1a082e4d9', name: 'Elgato Wave:3', image: 'data:image/jpeg;base64,', slug: 'elgato-wave-3' },

  ];

  return (
    <div className="microphone-page">
      <h2>Browse Microphones</h2>
      <div className="microphone-grid">
        {microphoneData.map((microphone) => (
          <Link to={`/product/${microphone.id}`} key={microphone.id} className="microphone-card">
            <img src={microphone.image} alt={microphone.name} />
            <h3>{microphone.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MicrophonePage;
