import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/PeripheralsPage.css'; // Updated CSS file path

const MonitorPage = () => {
  const monitorData = [
    { id: '67ff0fd7173e97a1a082e4df', name: 'LG 27GP950-B', image: 'data:image/jpeg;base64,', slug: 'lg-27gp950-b' },
  { id: '67ff0fd7173e97a1a082e4e0', name: 'Samsung Odyssey Neo G9', image: 'data:image/jpeg;base64,', slug: 'samsung-odyssey-neo-g9' },
  { id: '67ff0fd7173e97a1a082e4e1', name: 'ASUS ROG Swift PG279Q', image: 'data:image/jpeg;base64,', slug: 'asus-rog-swift-pg279q' },
  { id: '67ff0fd7173e97a1a082e4e2', name: 'Alienware AW3423DWF', image: 'data:image/jpeg;base64,', slug: 'alienware-aw3423dwf' },
  { id: '67ff0fd7173e97a1a082e4e3', name: 'BenQ MOBIUZ EX2710Q', image: 'data:image/jpeg;base64,', slug: 'benq-mobiuz-ex2710q' },

  ];

  return (
    <div className="monitor-page">
      <h2>Browse Monitors</h2>
      <div className="monitor-grid">
        {monitorData.map((monitor) => (
          <Link to={`/product/${monitor.id}`} key={monitor.id} className="monitor-card">
            <img src={monitor.image} alt={monitor.name} />
            <h3>{monitor.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MonitorPage;
