import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/CasePage.css'; // Assuming you want to use the same styles


const MousePage = () => {
  const mouseData = [
    { id: '67ff0ed9173e97a1a082e4c6', name: 'Logitech G Pro X Superlight', image: 'data:image/jpeg;base64,', slug: 'logitech-g-pro-x-superlight' },
  { id: '67ff0ed9173e97a1a082e4c7', name: 'Razer DeathAdder V3 Pro', image: 'data:image/jpeg;base64,', slug: 'razer-deathadder-v3-pro' },
  { id: '67ff0ed9173e97a1a082e4c8', name: 'Corsair Sabre RGB Pro Wireless', image: 'data:image/jpeg;base64,', slug: 'corsair-sabre-rgb-pro-wireless' },
  { id: '67ff0ed9173e97a1a082e4c9', name: 'SteelSeries Aerox 5 Wireless', image: 'data:image/jpeg;base64,', slug: 'steelseries-aerox-5-wireless' },
  { id: '67ff0ed9173e97a1a082e4ca', name: 'Glorious Model O 2 Wireless', image: 'data:image/jpeg;base64,', slug: 'glorious-model-o-2-wireless' },

  ];

  return (
    <div className="mouse-page">
      <h2>Browse Mice</h2>
      <div className="mouse-grid">
        {mouseData.map((mouse) => (
          <Link to={`/product/${mouse.id}`} key={mouse.id} className="mouse-card">
            <img src={mouse.image} alt={mouse.name} />
            <h3>{mouse.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MousePage;
