import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/PeripheralsPage.css'; // Assuming you want to use the same styles

const MousepadPage = () => {
  const mousepadData = [
    { id: '67ff0f39173e97a1a082e4d0', name: 'Razer Gigantus V2', image: 'data:image/jpeg;base64,', slug: 'razer-gigantus-v2' },
  { id: '67ff0f39173e97a1a082e4d1', name: 'Logitech G840 XL', image: 'data:image/jpeg;base64,', slug: 'logitech-g840-xl' },
  { id: '67ff0f39173e97a1a082e4d2', name: 'SteelSeries QcK Prism Cloth', image: 'data:image/jpeg;base64,', slug: 'steelseries-qck-prism-cloth' },
  { id: '67ff0f39173e97a1a082e4d3', name: 'HyperX Fury Ultra RGB', image: 'data:image/jpeg;base64,', slug: 'hyperx-fury-ultra-rgb' },
  { id: '67ff0f39173e97a1a082e4d4', name: 'Corsair MM300 PRO', image: 'data:image/jpeg;base64,', slug: 'corsair-mm300-pro' },

  ];

  return (
    <div className="mousepad-page">
      <h2>Browse Mousepads</h2>
      <div className="mousepad-grid">
        {mousepadData.map((mousepad) => (
          <Link to={`/product/${mousepad.id}`} key={mousepad.id} className="mousepad-card">
            <img src={mousepad.image} alt={mousepad.name} />
            <h3>{mousepad.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MousepadPage;
