import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/StoragePage.css'; // Import the CSS file

const storageData = [
  { id: '67ff0de2173e97a1a082e4ad', name: 'Samsung 990 Pro 2TB NVMe SSD', image: 'data:image/jpeg;base64,', slug: 'samsung-990-pro-2tb-nvme-ssd' },
  { id: '67ff0de2173e97a1a082e4ae', name: 'Western Digital Black SN850X 2TB NVMe SSD', image: 'data:image/png;base64,', slug: 'western-digital-black-sn850x-2tb-nvme-ssd' },
  { id: '67ff0de2173e97a1a082e4af', name: 'Seagate Exos X20 20TB HDD', image: 'data:image/jpeg;base64,', slug: 'seagate-exos-x20-20tb-hdd' },
  { id: '67ff0de2173e97a1a082e4b0', name: 'Crucial MX500 4TB SATA SSD', image: 'data:image/jpeg;base64,', slug: 'crucial-mx500-4tb-sata-ssd' },
  { id: '67ff0de2173e97a1a082e4b1', name: 'SanDisk Extreme Pro 1TB Portable SSD', image: 'data:image/jpeg;base64,', slug: 'sandisk-extreme-pro-1tb-portable-ssd' },

];

const StoragePage = () => {
  return (
    <div className="storage-page">
      <h2>Browse Storage</h2>
      <div className="storage-grid">
        {storageData.map((storage) => (
          <Link to={`/product/${storage.id}`} key={storage.id} className="storage-card">
            <img src={storage.image} alt={storage.name} />
            <h3>{storage.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default StoragePage;
